import 'zone.js';
import previewBase from '@galactik/storybook-config/preview-base';
const withAngularBackground = (StoryFn, context) => {
    const doc = globalThis.document;
    if (doc?.body) {
        doc.body.style.backgroundColor =
            context.globals.theme === 'dark'
                ? 'var(--abs-black)'
                : 'var(--color-background-surface)';
    }
    return StoryFn(context);
};
const baseDecorators = Array.isArray(previewBase.decorators)
    ? previewBase.decorators
    : previewBase.decorators
        ? [previewBase.decorators]
        : [];
const preview = {
    ...previewBase,
    decorators: [...baseDecorators, withAngularBackground]
};
export default preview;
