export const withDesignTokens = (StoryFn, context) => {
    const doc = globalThis.document;
    if (doc) {
        doc.documentElement.setAttribute('data-theme', context.globals.theme ?? 'light');
    }
    return StoryFn(context);
};
export default withDesignTokens;
