{
  title: 'Electrons/Avatar',
  component: AvatarComponent,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [AvatarComponent]
    })
  ],
  parameters: {
    layout: 'padded',
    docs: {
      description: {
        component: `@example
\`\`\`html
<!-- Avatar with initials (Figma colors by default) -->
<oc-avatar name="Alice Smith" size="medium"></oc-avatar>

<!-- Avatar with image -->
<oc-avatar 
  name="Clara White"
  src="https://example.com/photo.jpg"
  size="large">
</oc-avatar>

<!-- Different sizes -->
<oc-avatar name="John Doe" size="small"></oc-avatar>
<oc-avatar name="Jane Doe" size="medium"></oc-avatar>
<oc-avatar name="Jack Doe" size="large"></oc-avatar>

<!-- Profile with avatar -->
<div style="display: flex; align-items: center; gap: 12px">
  <oc-avatar name="Emma Wilson" size="large"></oc-avatar>
  <div>
    <div style="font-weight: 600">Emma Wilson</div>
    <div style="font-size: 12px; color: var(--color-font-neutral-muted)">Designer</div>
  </div>
</div>
\`\`\`
`
      }
    }
  },
  argTypes: {
    size: {
      control: { type: 'inline-radio' },
      options: ['small', 'medium', 'large'],
      description: 'Avatar size (24px, 32px, 48px)'
    },
    name: {
      control: 'text',
      description: 'Full name used to generate initials'
    },
    src: {
      control: 'text',
      description: 'Image URL (falls back to initials if empty)'
    },
    avatarColor: {
      control: 'select',
      options: colorOptions,
      description: 'Avatar color (synced MCP tokens)'
    },
    variant: {
      control: 'inline-radio',
      options: ['primary', 'secondary'],
      description: 'Variant'
    },
    icon: {
      control: 'text',
      description: 'Font Awesome icon class (ex: "fas fa-user")'
    }
  },
  args: {
    name: 'Xavier Xu',
    size: 'medium',
    avatarColor: 'sage',
    variant: 'primary'
  }
}

{
  title: 'Electrons/Badge',
  component: BadgeComponent,
  tags: ['autodocs'],
  decorators: [
    moduleMetadata({
      imports: [BadgeComponent]
    })
  ],
  parameters: {
    layout: 'padded',
    docs: {
      description: {
        component: `@example
\`\`\`html
<!-- Badge with number (default mode) -->
<oc-badge [label]="5" color="sage" style="primary" size="sm"></oc-badge>

<!-- Badge with custom colors -->
<oc-badge [label]="12" color="info" style="secondary" size="lg"></oc-badge>

<!-- Badge with icon (Font Awesome) -->
<oc-badge 
  color="error" 
  style="primary" 
  size="lg"
  mode="icon">
  <i class="fa-solid fa-bell"></i>
</oc-badge>

<!-- Badge in a notification -->
<div style="display: flex; align-items: center; gap: 8px">
  <span>Notifications</span>
  <oc-badge [label]="3" color="cherry" style="primary" size="xs"></oc-badge>
</div>
\`\`\`
`
      }
    }
  },
  argTypes: {
    size: {
      options: ['xs', 'sm', 'lg'],
      control: { type: 'inline-radio' },
      description: 'Badge size (xs/sm/lg tokens)'
    },
    color: {
      options: colorOptions,
      control: { type: 'select' },
      description: 'Theme (Portal semantic tokens)'
    },
    variant: {
      options: ['primary', 'secondary'],
      control: { type: 'inline-radio' },
      description: 'Filled or outlined style'
    },
    label: {
      control: { type: 'text' },
      description: 'Displayed number'
    },
    mode: {
      options: ['number', 'icon'],
      control: { type: 'inline-radio' },
      description: 'Display mode'
    }
