(
  StoryFn: StoryFn<Renderer>,
  context: StoryContext<Renderer>
) => {
  const doc = (globalThis as { document?: Document }).document;
  if (doc?.body) {
    doc.body.style.backgroundColor =
      context.globals.theme === 'dark'
        ? 'var(--abs-black)'
