          [checked]="allChecked()"
          [indeterminate]="someChecked()"
          (checkedChange)="handleSelectAll($event)"
          label="Select all"
        ></oc-checkbox>
        <div style="height: 1px; background-color: var(--color-border-base); margin: 4px 0;"></div>
